INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395312, 'Clopidogrel', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 2C19 may reduce the efficacy of clopidogrel, whose antiplatelet effect is dependent in part on bioactivation by the isoenzyme to a pharmacologically active metabolite.', 'DDInter', 1767317475, 'Based on existing data, it may be advisable to closely monitor the therapeutic efficacy of clopidogrel during concomitant treatment with CYP450 2C19 inhibitors.', 'Metabolism', 'Based on existing data, it may be advisable to closely monitor the therapeutic efficacy of clopidogrel during concomitant treatment with CYP450 2C19 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
