INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395390, 'Clorazepic acid', 'Boceprevir', 'Moderate', 'Coadministration with boceprevir may increase the plasma concentrations of drugs that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by boceprevir.', 'DDInter', 1767317475, 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever boceprevir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
