INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119101, 'Irinotecan', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or UGT1A1 may increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors. Patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119239/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Corona G, Vaccher E, Sandron S, et al. "Lopinavir-ritonavir dramatically affects the pharmacokinetics of irinotecan in HIV patients with Kaposi''s sarcoma." Clin Pharmacol Ther 83 (2008): 601-6[4] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals, Cambridge, MA.[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cf