INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119112, 'Irinotecan', 'Ozanimod', 'Major', 'Coadministration of ozanimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects. Ozanimod causes reversible sequestration of lymphocytes in lymphoid tissues. The safety and efficacy of ozanimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod.', NULL, 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod. When switching from drugs with prolonged immune effects to ozanimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119250/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119113, 'Irinotecan', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or UGT1A1 may increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors. Patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119251/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Corona G, Vaccher E, Sandron S, et al. "Lopinavir-ritonavir dramatically affects the pharmacokinetics of irinotecan in HIV patients with Kaposi''s sarcoma." Clin Pharmacol Ther 83 (2008): 601-6[4] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals, Cambridge, MA.[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Phansalker S, Desai AA, Bell D, et al "High-priority drug-drug interactions for use in electronic health records." J Am Med Inform Assoc 19 (2012): 735-43[7] "Product Information. Camptosar (irinotecan)." Pharmacia and Upjohn, Kalamazoo, MI.[8] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119114, 'Irinotecan', 'Palifermin', 'Moderate', 'INTERVAL: In a clinical trial, administration of palifermin within 24 hours of chemotherapy resulted in increased severity and duration of oral mucositis, presumably due to increased sensitivity of the rapidly dividing epithelial cells in the immediate post-chemotherapy period.', NULL, 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', NULL, 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119252/', '[1] "Product Information. Kepivance (palifermin)." Shire US Inc, Florence, KY.', 1767365207);
