INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119117, 'Irinotecan', 'Pazopanib', 'Moderate', 'Coadministration with pazopanib may increase the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. The proposed mechanism is pazopanib inhibition of UGT1A1 and CYP450 3A4, the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism. Pazopanib is a potent inhibitor of UGT1A1 and weak inhibitor of CYP450 3A4 in vitro. However, its potential effects on the pharmacokinetics of irinotecan have not been studied.', NULL, 'Caution is advised when irinotecan is prescribed with pazopanib.', NULL, 'Caution is advised when irinotecan is prescribed with pazopanib. Patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119255/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals, Cambridge, MA.[5] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[6] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119118, 'Irinotecan', 'Pegfilgrastim', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119256/', '[1] "Product Information. Neulasta (pegfilgrastim)." Amgen, Thousand Oaks, CA.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767365207);
