INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119158, 'Irinotecan', 'Sorafenib', 'Moderate', 'Coadministration with sorafenib may increase the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. The proposed mechanism is sorafenib inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of irinotecan and SN-38, respectively.', NULL, 'Sorafenib product labeling recommends caution during coadministration with irinotecan.', NULL, 'Sorafenib product labeling recommends caution during coadministration with irinotecan. High plasma levels of irinotecan and SN-38 may result in increased toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119296/', '[1] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Mross K, Steinbild S, Baas F, et al. "Drug-drug interaction pharmacokinetic study with the Raf kinase inhibitor (RKI) BAY 43-9006 administered in combination with irinotecan (CPT-11) in patients with solid tumors." Int J Clin Pharmacol Ther 41 (2003): 618-9[3] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767365207);
