INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119356, 'Isavuconazonium', 'Fesoterodine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of fesoterodine should be considered. Because 5-hydroxymethyl tolterodine is also metabolized by CYP450 2D6, the clinical significance of the interaction may be greater in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent) who may rely more on the 3A4 metabolic pathway for clearance of the drug.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary. Patients should be advised to notify their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119494/', '[1] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119357, 'Isavuconazonium', 'Fexofenadine', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), such as dabigatran. The proposed mechanism is isavuconazole inhibition of P-gp-mediated efflux of these drugs.', NULL, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever isavuconazonium sulfate is added to or withdrawn from therapy with these drugs. Patients receiving concomitant dabigatran should be monitored closely for the development of bleeding complications and the dosage of dabigatran adjusted as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119495/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Mekinist (trametinib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767365207);
