INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119370, 'Isavuconazonium', 'Gefitinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of gefitinib, which is primarily metabolized by the isoenzyme. According to the product labeling, administration of gefitinib (250 mg single dose) with the potent inhibitor itraconazole (200 mg once a day for 12 days) increased the mean gefitinib systemic exposure (AUC) by 88% in healthy male volunteers.', NULL, 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, 'Caution is advised if gefitinib is administered with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience possible symptoms of gefitinib toxicity such as severe diarrhea, nausea, dyspnea, cough, and fever.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119508/', '[1] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[2] "Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119371, 'Isavuconazonium', 'Gilteritinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of gilteritinib, which is a substrate of both the isoenzyme and the efflux transporter.', NULL, 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections and stomatitis, and the gilteritinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119509/', '[1] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365207);
