INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119373, 'Isavuconazonium', 'Granisetron', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119511/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119374, 'Isavuconazonium', 'Grepafloxacin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', NULL, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, peripheral edema, hypokalemia, hypomagnesemia, and hepatotoxicity. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored, as isavuconazole itself is reportedly a moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119512/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.', 1767365207);
