INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119497, 'Isavuconazonium', 'Rivaroxaban', 'Moderate', 'Coadministration with weak or moderate dual inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of rivaroxaban, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications.', NULL, 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119635/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.[2] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[3] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119498, 'Isavuconazonium', 'Romidepsin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of romidepsin, which is a substrate of the efflux transporter. Although there are no formal drug interaction studies, the interaction may increase the risk of hematologic toxicities such as anemia, leukopenia, and thrombocytopenia as well as electrocardiographic changes such as QT interval prolongation and T-wave and ST-segment changes.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors. Pharmacologic response to romidepsin should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the romidepsin dosage adjusted as necessary. Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119636/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.', 1767365207);
