INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119501, 'Isavuconazonium', 'Rufinamide', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme may decrease the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', NULL, 'Caution is advised during concomitant use with CYP450 3A4 inducers; the potential for decreased efficacy of isavuconazonium sulfate should be considered.', NULL, 'Caution is advised during concomitant use with CYP450 3A4 inducers; the potential for decreased efficacy of isavuconazonium sulfate should be considered. Caution and clinical monitoring should also be considered during concomitant use of isavuconazonium sulfate and medicines known to shorten the QT interval.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119639/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.[3] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119502, 'Isavuconazonium', 'Ruxolitinib', 'Minor', 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme. No dosage adjustment is recommended when ruxolitinib is coadministered with mild or moderate CYP450 3A4 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119640/', '[1] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.', 1767363024);
