INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119912, 'Isoflurane', 'Nitrous oxide', 'Minor', 'Coadministration of 70% nitrous oxide reduces the dosage requirements for halogenated inhalation anesthetics. Minimum alveolar concentration (MAC) values can be reduced from 0.75% to 0.29% for halothane, from 1.68% to 0.6% for enflurane, and from 1.15% to 0.5% for isoflurane. The MAC equivalent dose for sevoflurane is reduced by approximately 50% in the presence of 50% nitrous oxide.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120050/', '[1] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[2] "Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363024);
