INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (119939, 'Isoprenaline', 'Amoxapine', 'Moderate', 'The concomitant use of isoproterenol and tricyclic antidepressants may increase the risk of arrhythmias, especially at high doses. The mechanism has not been delineated. Tachycardia has been reported in clinical studies.', NULL, 'Some authorities suggest that the initial rate and dose of isoproterenol should be reduced in patients taking tricyclic antidepressants, and then titrated, as is usual according to clinical response to avoid the risk of developing hypertension or arrhythmias.', NULL, 'Some authorities suggest that the initial rate and dose of isoproterenol should be reduced in patients taking tricyclic antidepressants, and then titrated, as is usual according to clinical response to avoid the risk of developing hypertension or arrhythmias. Patients receiving this combination should be monitored and advised to notify their physician if they experience a fast or irregular pulse.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120077/', '[1] Kadar D "Letter: Amitriptyline and isoproterenol: fatal drug combination." Can Med Assoc J 112 (1975): 556-7[2] Boakes AJ, Laurence DR, Teoh PC, Barar FS, Benedikter LT, Pritchard BN "Interactions between sympathomimetic amines and antidepressant agents in man." Br Med J 1 (1973): 311-5[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363024);
