INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120455, 'Isosorbide mononitrate', 'Selexipag', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120593/', '[1] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[2] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Buchdahl RM, Taylor P, Warner JD "Nebulised ribavirin for adenovirus pneumonia." Lancet 2 (1985): 1070-1[5] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120456, 'Isosorbide mononitrate', 'Sildenafil', 'Major', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of organic nitrates. Severe hypotension, syncope, or myocardial ischemia may result from use of the combination. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE5 inhibitors prevent degradation of cGMP while nitrates promote its synthesis.', NULL, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated.', NULL, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated. A suitable time interval following sildenafil or vardenafil use for the safe administration of nitrates has not been determined. For patients treated with avanafil or tadalafil, at least 12 hours after the last dose of avanafil and 48 hours after the last dose of tadalafil are recommended before nitrate administration, should it be deemed medically necessary in a life-threatening situation. Even then, nitrates should only be administered under close medical supervision with appropriate hemodynamic monitoring. Patients should be advised to seek immediate medical attention if they experience anginal chest pain after taking a PDE5 inhibitor.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120594/', '[1] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[4] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[5] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767363024);
