INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240164, 'Cladribine', 'Lisocabtagene maraleucel', 'Major', 'The use of cladribine with other immunosuppressive or myelosuppressive agents may increase the risk of infections.  Cladribine alone may cause severe and prolonged myelosuppression, lymphopenia, and opportunistic infections.', NULL, 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible.', NULL, 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible.  Acute short-term therapy with corticosteroids can be administered.  Caution is advised if IV cladribine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.  Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.  In patients who have previously been treated with immunomodulatory or immunosuppressive drugs, consider potential additive effect, mode of action, and duration of effect of the other drugs prior to initiation of cladribine.  Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259791/', '[1] "Product Information. Leustatin (cladribine)." Ortho Biotech Inc  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
