INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240166, 'Coccidioides immitis spherule', 'Lisocabtagene maraleucel', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity.  Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259793/', '[1] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc  (2001):[2] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc  (2001):[3] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc  (2001):[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals  (2001):[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals  (2001):[6] "Product Information. Tuberculin Tine Test (tuberculin purified protein derivative)." Connaught Laboratories Inc', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240167, 'Deferiprone', 'Lisocabtagene maraleucel', 'Major', 'Coadministration of deferiprone and other drugs that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', NULL, 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.', NULL, 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.  Otherwise, close monitoring of absolute neutrophil count (ANC) is recommended if coadministration is required.  The ANC should be measured prior to initiating deferiprone and weekly during therapy.  The manufacturer recommends that treatment be interrupted if neutropenia (ANC between 0.5 and 1.5 x 10^9/L) or infection develops.  Patients should be advised to immediately discontinue deferiprone as well as all other medications with a potential to cause neutropenia and seek medical attention if they experience symptoms indicative of infection such as fever, sore throat, and flu-like symptoms.  A complete blood cell (CBC) count, including a white blood cell (WBC) count corrected for the presence of nucleated red blood cells, an absolute neutrophil count (ANC), and a platelet count should be obtained daily until recovery.  For patients who develop agranulocytosis (ANC less than 0.5 x 10^9/L), hospitalization should be considered, and deferiprone should not be resumed following recovery unless potential benefits outweigh the risks.  Likewise, patients who develop neutropenia with deferiprone should not be rechallenged unless potential benefits outweigh the risks.', 'Synergism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259795/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Ferriprox (deferiprone)." ApoPharma USA Inc  (2011):', 1767369485);
