INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240340, 'Budesonide', 'Yellow Fever Vaccine', 'Moderate', 'The administration of live, attenuated virus or bacterial vaccines during corticosteroid therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored.  The interval depends on the dosage and duration of corticosteroid therapy administered, but may be at least 3 months in most cases.  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, high-dose corticosteroid therapy should not be initiated for at least 2 weeks.  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Synergism', 'Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Tiotropium, Beclomethasone dipropionate, Terbutaline, Aclidinium, Glycopyrronium, Mometasone, Fluticasone, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/260714/', '[1] "Product Information. Mumpsvax (mumps virus vaccine)." Merck & Co., Inc  (2022):[2] "Product Information. Meruvax II (rubella virus vaccine)." Merck & Co., Inc  (2022):[3] "Product Information. Attenuvax (measles virus va