INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240489, 'Drospirenone', 'Verapamil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', 'Norelgestromin, Norethisterone, Etonogestrel, Megestrol acetate, Norgestimate, Etynodiol, Medroxyprogesterone acetate, Desogestrel, Dienogest, Estetrol, Ethinylestradiol, More', 'Felodipine, Hydrochlorothiazide, Indapamide, Bisoprolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261188/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Slynd (drospirenone)." Exeltis USA, Inc.  (2022):[3] "Product Information. Angeliq (drospirenone-estradiol)." Bayer Pharmaceutical Inc  (2017):[4] "Product Information. Slinda (drospirenone)." Besins Healthcare Australia Pty Ltd  (2021):[5] "Product Information. Angeliq 1/2 (drospirenone-estradiol)." Bayer Australia Limited  (2021):[6] "Product Information. Slynd (drospirenone)." Duchesnay Inc  (2021):[7] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb  (2022):[8] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Australia Pty Ltd  (2023):[9] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Pharmaceuticals Ltd  (2021):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240490, 'Verapamil', 'Melatonin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may substantially increase the plasma concentrations of melatonin.  Data from available studies suggest that melatonin is primarily metabolized by CYP450 1A2, with possible contribution from CYP450 2C19 and 2C9.  Coadministration of fluvoxamine, a potent CYP450 1A2 inhibitor that also inhibits CYP450 2C19 and 2C9, increased melatonin systemic exposure (AUC) by 17-fold and maximum plasma concentration (Cmax) by 12-fold.  Contraceptives containing ethinyl estradiol, which can inhibit CYP450 1A2, may lead to a 4- to 5-fold increase in melatonin concentration.', NULL, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.', NULL, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.  Patients should be monitored closely for adverse effects such as excessive drowsiness, headache, lethargy, dizziness, irritability, nervousness, restlessness, anxiety, abdominal pain, and dyspepsia.', 'Metabolism', 'Scopolamine, Estazolam, Ethchlorvynol, Dichloralphenazone, Valerian', 'Felodipine, Enalapril, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261204/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
