INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240727, 'Citalopram', 'Turmeric', 'Moderate', 'Turmeric may potentiate the bleeding risk associated with drugs that interfere with platelet function or coagulation.', NULL, 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.', NULL, 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.  Clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', 'Maprotiline, St. John''s Wort, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, Esketamine, Phenelzine, Desipramine, Tranylcypromine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/262956/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000):  1221-7; quiz 1228-30[2] Abebe W "Herbal medication: potential for adverse interactions with analgesic drugs." J Clin Pharm Ther 27 (2002):  391-401[3] Yang X, Thomas DP, Zhang X, et al. "Curcumin inhibits platelet-derived growth factor-stimulated vascular smooth muscle cell function and injury-induced neointima formation." Arterioscler Thromb Vasc Biol 26 (2006):  85-90[4] New Zealand Medicines and Medical Devices Safety Authority "Medsafe Monitoring Communication: Beware turmeric/curcumin containing