INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240912, 'Pralsetinib', 'Olodaterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Cromoglicic acid, Ipratropium, Tiotropium, Beclomethasone dipropionate', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263570/', '[1] Whyte KF, Addis GJ, Whitesmith R, Reid JL "The mechanism of salbutamol-induced hypokalaemia." Br J Clin Pharmacol 23 (1987):  65-71[2] Larsson S, Svedmyr N "Bronchodilating effect and side effects of beta2- adrenoceptor stimulants by different modes of administration (tablets, metered aerosol, and combinations thereof). A study with salbutamol inasthmatics." Am Rev Respir Dis 116 (1977