INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240922, 'Tolevamer', 'Sufentanil', 'Moderate', 'Coadministration with medications that can cause constipation such as opioids may increase the risk of intestinal injuries associated with the use of sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.  The prescribing information recommends avoiding the use of sodium polystyrene sulfonate in patients who are at risk for developing constipation or impaction, including those with a history of impaction, chronic constipation, inflammatory bowel disease, ischemic colitis, vascular intestinal atherosclerosis, previous bowel resection, or bowel obstruction, as well as those who have not had a bowel movement post-surgery.  If clinically significant constipation develops, treatment with sodium polystyrene sulfonate should be discontinued until normal bowel motion is resumed.  Concomitant administration of sorbitol is not recommended.', 'Synergism', 'Halothane, Thiopental, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, Sevoflurane, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Deferoxamine, Levoleucovorin, Arginine, Methionine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263602/', '[1] Foresti V "Intestinal obstruction due to kayexalate in a patient concurrently treated with aluminum hydroxide and morphine su