INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240965, 'Esketamine', 'Trimipramine', 'Major', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', NULL, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants or other drugs that can cause sedation or dizziness.', NULL, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants or other drugs that can cause sedation or dizziness.  Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting.  Patients should be instructed not to engage in potentially hazardous activities that require complete mental alertness and motor coordination, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Trimipramine, Desflurane, Enflurane, Etomidate, Nitrous oxide', 'Citalopram, Sertraline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Viloxazine, Esketamine, Fluvoxamine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263729/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] "Product Information. Spravato (esketamine)." Janssen Pharmaceuticals  (2019):', 1767369485);
