INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121061, 'Itraconazole', 'Triazolam', 'Major', 'Coadministration with itraconazole or ketoconazole may significantly increase the plasma concentrations and pharmacologic effects of benzodiazepines that are primarily metabolized by CYP450 3A4. The mechanism is increased bioavailability and/or decreased clearance due to inhibition of intestinal and hepatic CYP450 3A4 by the azole antifungal agents. Pharmacodynamic changes associated with the interaction include increased and prolonged sedation, enhanced benzodiazepine-related EEG effects, and increased impairment of psychomotor performance. The interaction is subject to a high degree of interpatient variability.', NULL, 'The concomitant use of itraconazole or ketoconazole with alprazolam, oral midazolam, and triazolam is considered contraindicated.', NULL, 'The concomitant use of itraconazole or ketoconazole with alprazolam, oral midazolam, and triazolam is considered contraindicated. Some authorities consider concomitant administration of triazolam or oral midazolam and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. Fluconazole and miconazole are weaker inhibitors of CYP450 3A4 but may still cause the interaction, while terbinafine is not an inhibitor of CYP450 3A4 and has been shown to have no effect on the pharmacokinetics of midazolam and triazolam. Alternatively, benzodiazepines that are not metabolized by CYP450 3A4 (e.g., lorazepam, oxazepam, temazepam) may be considered in patients requiring itraconazole or ketoconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121199/', '[1] "Pro