INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121259, 'Ivabradine', 'Ribociclib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme. Due to its bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased when ivabradine is used with drugs that can prolong the QT interval.', NULL, 'Concomitant use of ivabradine with moderate CYP450 3A4 inhibitors that can also prolong the QT interval such as crizotinib, dronedarone, fluconazole, lefamulin (when administered orally), and mifepristone should generally be avoided.', NULL, 'Concomitant use of ivabradine with moderate CYP450 3A4 inhibitors that can also prolong the QT interval such as crizotinib, dronedarone, fluconazole, lefamulin (when administered orally), and mifepristone should generally be avoided. If coadministration is required, a dosage reduction of ivabradine should be considered. Some authorities recommend initiating ivabradine at a dosage of 2.5 mg twice daily when used with moderate CYP450 3A4 inhibitors, provided the resting heart rate is at or above 70 beats per minute and frequent cardiac monitoring is performed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121397/', '[1] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT 