INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121334, 'Ivermectin', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121472/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121335, 'Ivermectin', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or organic anion transporting polypeptide protein (OATP) 1B1 and 1B3. Letermovir has been shown to be a reversible inhibitor of CYP450 2C8 in vitro, although its effect on CYP450 2C8 substrates has not been evaluated clinically. Letermovir is also a time-dependent inhibitor and inducer of CYP450 3A4 in vitro.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy. Moreover, clinicians should be aware that the magnitude of CYP450 3A- and OATP1B1/3-mediated drug interactions with coadministered drugs may be different when letermovir is used with cyclosporine. The combined effect of the two drugs on CYP450 3A4 may be similar to that of a strong CYP450 3A4 inhibitor, hence clinicians should refer to the prescribing information for dosing recommendations of the CYP450 3A4 substrate with a strong CYP450 3A4 inhibitor. Similarly, letermovir and cyclosporine may demonstrate some additive effects on OATP1B1 inhibition, although cyclosporine by itself is already a strong OATP1B1/3 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121473/', '[1] "Product Information. Prevymis (letermovir)." Merck & Company Inc, Whitehouse Station, NJ.', 1767365207);
