INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121397, 'Etravirine', 'Ivosidenib', 'Moderate', 'Coadministration of etravirine with a drug that is both a substrate as well as inducer of CYP450 3A4 may result in decreased plasma concentrations of both drugs. Etravirine itself is also a substrate and inducer of CYP450 3A4. Theoretically, metabolism of etravirine and the coadministered drug may be mutually induced when used in combination.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if etravirine is prescribed in combination with a drug that is an inducer of CYP450 3A4.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if etravirine is prescribed in combination with a drug that is an inducer of CYP450 3A4. Close clinical and laboratory monitoring of antiretroviral response is recommended. In addition, dosage adjustments may be required for the coadministered drug if it is also a substrate of CYP450 3A4.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121535/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5]