INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396306, 'Clozapine', 'Troglitazone', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396307, 'Clozapine', 'Troleandomycin', 'Moderate', 'The concomitant administration of some macrolides may result in decreased clearance of clozapine and increased risk of toxicity. The mechanism of this interaction is suspected to be inhibition of the CYP450 hepatic metabolism of clozapine.', 'DDInter', 1767317475, 'While clinical data are limited, clinical and laboratory monitoring may be appropriate whenever a macrolide is added to or withdrawn from therapy. Alternatively, azithromycin and dirithromycin may be considered, as they do not inhibit CYP450 and are not expected to affect clozapine metabolism. Patients should be advised to report symptoms such as somnolence, disorientation, slurred speech, seizures, uncontrolled movements, dizziness, irregular heartbeat, palpitations, fever, or hypersalivation to their physician.', 'Metabolism', 'While clinical data are limited, clinical and laboratory monitoring may be appropriate whenever a macrolide is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
