INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121531, 'Ponatinib', 'Ivosidenib', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of ponatinib underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ponatinib is unknown.', 'Metabolism', 'Tivozanib, Futibatinib, Fedratinib, Regorafenib, Trametinib, Neratinib, Nintedanib, Afatinib, Dacomitinib, Pexidartinib, Alectinib, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Enasidenib, Porfimer sodium, Aflibercept, Trastuzumab, Hydroxyurea, Estramustine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121669/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121532, 'Posaconazole', 'Ivosidenib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivosidenib, which is primarily metabolized by the isoenzyme. Coadministration with ivosidenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4, including many of the known potent inhibitors of the isoenzyme such as clarithromycin, conivaptan, delavirdine, idelalisib, itraconazole, ketoconazole, nefazodone, posaconazole, telithromycin, and all protease inhibitors. Ivosidenib is an inducer of CYP450 3A4 and induces its own metabolism following multiple dosing.', NULL, 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided.', NULL, 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided. If coadministration is required, a reduction of the ivosidenib dosage to 250 mg once daily is recommended. Following discontinuation of the potent inhibitor for at least 5 half-lives, ivosidenib therapy should be returned to the recommended dosage of 500 mg once daily. Monitoring of electrocardiograms and electrolytes should be conducted regularly.', 'Metabolism', 'Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Belzutifan, Mitotane, Niraparib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121670/', '[1] "Product Information. Tibsovo (ivosidenib)." Agios Pharmaceuticals, Cambridge, MA.[2] "Product Information. Tibsovo (ivosidenib)." Agios Pharmaceuticals, Cambridge, MA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121533, 'Praziquantel', 'Ivosidenib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of praziquantel, which is a substrate of the isoenzyme.', NULL, 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved.', NULL, 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved. Alternative agents for schistosomiasis should be considered whenever possible in patients receiving treatment with a CYP450 3A4 inducer. Otherwise, clinical response to praziquantel should be closely monitored.', 'Metabolism', 'Oxamniquine', 'Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121671/', '[1] Ridtitid W, Wongnawa M, Mahatthanatrakul W, Punyo J, Sunbhanich M "Rifampin markedly decreases plasma concentrations of praziquantel in healthy volunteers." Clin Pharmacol Ther 72 (2002): 505-13[2] Bittencourt PR, Gracia CM, Martins R, et al "Phenytoin and carbamazepine decrease oral bioavailability of praziquantel." Neurology 42 (1992): 492-6[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Biltricide (praziquantel)." Bayer, West Haven, CT.[5] Castro N, Medina R, Sotelo J, Jung H "Bioavailability of praziquantel increases with concomitant administration of food." Antimicrob