INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121940, 'Troglitazone', 'Ixazomib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ixazomib, which is a substrate of the isoenzyme. When ixazomib was administered with the potent CYP450 3A4 inducer rifampin, ixazomib peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 54% and 74%, respectively.', NULL, 'The potential for diminished pharmacologic effects of ixazomib should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of ixazomib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Enasidenib, Mitotane, Niraparib, Porfimer sodium, Selinexor, Aflibercept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122078/', '[1] "Product Information. Ninlaro (ixazomib)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[3] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[4] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[5] "Position Statement: evidence-based nutrition principles and recommenda