INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396594, 'Felodipine', 'Cobimetinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of cobimetinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317475, 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities and rhabdomyolysis, and the cobimetinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
