INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396775, 'Iobenguane (I-123)', 'Cocaine (topical)', 'Moderate', 'Coadministration with drugs that block norepinephrine uptake or deplete norepinephrine stores may decrease iobenguane I-123 uptake in neuroendocrine tumors and lead to false-negative imaging results.', 'DDInter', 1767317475, 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', 'Others', 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396776, 'Iobenguane (I-131)', 'Cocaine (topical)', 'Major', 'Coadministration with drugs that reduce catecholamine uptake or deplete catecholamine stores may interfere with iobenguane I-131 uptake into neuroendocrine tumors such as pheochromocytoma and paraganglioma that express high levels of norepinephrine transporter on their cell surfaces. Since iobenguane is similar in structure to norepinephrine and is subject to the same uptake and accumulation pathways as norepinephrine, drugs that alter norepinephrine disposition in adrenergic nerve terminals and presynaptic storage vesicles will likewise affect iobenguane.', 'DDInter', 1767317475, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131. Do not administer these drugs until at least 7 days after each iobenguane I-131 dose. Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396777, 'Ioflupane I-123', 'Cocaine (topical)', 'Moderate', 'Drugs that bind to the dopamine transporter with high affinity may interfere with the image obtained using ioflupane I-123, which also binds to the dopamine transporter. These drugs include amoxapine, amphetamine, benztropine, bupropion, buspirone, cocaine, diethylpropion, ketamine, mazindol, methamphetamine, methylphenidate, modafinil, pemoline, phencyclidine (PCP), phentermine, phenylpropanolamine, pipradrol, selegiline, sertraline, and trihexyphenidyl. Selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs) may increase or decrease ioflupane binding to the dopamine transporter. Whether discontinuation of these drugs prior to ioflupane I-123 administration may help to minimize potential interference is unknown.', 'DDInter', 1767317475, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', 'Others', 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396778, 'Isocarboxazid', 'Cocaine (topical)', 'Major', 'Cocaine exerts an indirect sympathomimetic effect and may precipitate hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs), as has been reported with other indirect- or mixed-acting sympathomimetic agents such as amphetamine and ephedrine. The mechanism involves a synergistic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and decreased reuptake of catecholamines (indirect sympathomimetic activity).', 'DDInter', 1767317475, 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with sympathomimetic agents.', 'Synergism', 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
