INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396820, 'Cocaine (topical)', 'Procarbazine', 'Major', 'Cocaine exerts an indirect sympathomimetic effect and may precipitate hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs), as has been reported with other indirect- or mixed-acting sympathomimetic agents such as amphetamine and ephedrine. The mechanism involves a synergistic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and decreased reuptake of catecholamines (indirect sympathomimetic activity).', 'DDInter', 1767317475, 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with sympathomimetic agents.', 'Synergism', 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
