INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396845, 'Cocaine (topical)', 'Voriconazole', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of drugs that are substrates of CYP450 2C19, 2C9, and/or 3A4. The mechanism is decreased clearance due to inhibition of those isoenzymes by voriconazole. Increased plasma levels and/or pharmacologic effects of drugs such as cyclosporine, sirolimus, tacrolimus (3A4 substrates), and warfarin (2C9 substrate) have been reported during coadministration with voriconazole.', 'DDInter', 1767317475, 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voriconazole is added to or withdrawn from therapy. The manufacturer specifically recommends that a dosage reduction be considered for benzodiazepines, statins, long-acting opiates, and vinca alkaloids that are metabolized by the affected isoenzymes when used with voriconazole.', 'Metabolism', 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396846, 'Codeine', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6. The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 1767317475, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1396847, 'Efavirenz', 'Codeine', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', 'DDInter', 1767317475, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
