INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122418, 'Warfarin', 'Protein C', 'Moderate', 'Simultaneous administration of protein C concentrate with tissue plasminogen activators and/or anticoagulants may increase the risk of bleeding. The mechanism of interaction is unknown. Several bleeding episodes have been observed in clinical studies of protein C concentrate for which concurrent anticoagulant medication may have been responsible. However, a contributory effect by protein C cannot be completely ruled out.', NULL, 'Caution is advised if protein C concentrate is used in combination with tissue plasminogen activators and/or anticoagulants.', NULL, 'Caution is advised if protein C concentrate is used in combination with tissue plasminogen activators and/or anticoagulants. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122556/', '[1] "Product Information. Ceprotin (protein C)." Baxter Healthcare Corporation, Deerfield, IL.', 1767363024);
