INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122424, 'Warfarin', 'Ramucirumab', 'Major', 'Coadministration of ramucirumab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', NULL, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously.', NULL, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122562/', '[1] "Product Information. Cyramza (ramucirumab)." Eli Lilly and Company, Indianapolis, IN.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122425, 'Warfarin', 'Ranitidine (bismuth citrate)', 'Moderate', 'Limited data suggest that ranitidine may occasionally alter the hypoprothrombinemic effect of coumarin anticoagulants. There have been rare reports of altered prothrombin time or INR (both increases and decreases) during coadministration of ranitidine and warfarin. The mechanism of interaction is unknown. Ranitidine at usual therapeutic dosages has not been shown to significantly affect CYP450-mediated metabolism.', NULL, 'Until more data are available, caution may be advisable if ranitidine is used in combination with coumarin anticoagulants.', NULL, 'Until more data are available, caution may be advisable if ranitidine is used in combination with coumarin anticoagulants. The INR should be checked regularly and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of ranitidine in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122563/', '[1] O''Reilly RA "Comparative interaction of cimetidine and ranitidine with racemic warfarin in man." Arch Intern Med 144 (1984): 989-91[2] "Product Information. Zantac (ranitidine)." Glaxo Wellcome, Research Triangle Park, NC.[3] Powell JR, Donn KH "Histamine H