INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1397037, 'Ibrutinib', 'Colchicine', 'Moderate', 'Coadministration with ibrutinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter. In vitro studies indicate that ibrutinib is not a substrate for P-gp, and systemic ibrutinib is unlikely to be an inhibitor of P-gp at clinical doses.', 'DDInter', 1767317475, 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range. Dosage reduction and/or dosage interval adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ibrutinib is added to or withdrawn from therapy. Some authorities recommend that narrow therapeutic range P-gp substrates, such as digoxin, should be administered at least 6 hours before or after ibrutinib.', 'Absorption', 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
