INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1397079, 'Colchicine', 'Ocrelizumab', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.', 'DDInter', 1767317475, 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy. In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy. Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1397080, 'Colchicine', 'Olaparib', 'Moderate', 'Based on in vitro inhibition data, coadministration with olaparib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 (e.g., cisapride, ergot alkaloids, fentanyl, lovastatin, oral midazolam, pimozide, quetiapine, simvastatin, triazolam, vinca alkaloids), P-gp (e.g., colchicine, dabigatran, digoxin), breast cancer resistance protein (BCRP) (e.g., rosuvastatin), OATP1B1 (e.g., eluxadoline, glyburide, repaglinide, statins, valsartan), or organic cation transporter 1 or 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism is decreased clearance due to inhibition of the corresponding metabolizing enzyme and/or efflux/uptake transporter by olaparib.', 'DDInter', 1767317475, 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever olaparib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
