INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (245007, 'Griseofulvin', 'Benzhydrocodone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, methadone, and oxycodone.  Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer.  Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Salicylic acid, Selenium Sulfide, Thiabendazole, Nystatin, Ciclopirox, Econazole, Flucytosine, Tioconazole', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288865/', '[1] Holmes VF "Rifampin-induced methadone withdrawal in AIDS." J Clin Psychopharmacol 10 (1991):  443-4[2] Liu S-J, Wang RI "Case report of barbiturate-induced enhancement of methadone metabolism and withdrawal syndrome." Am J Psychiatry 141 (1984):  1287-8[3] Bell J, Seres V, Bowron P, Lewis J, Batey R "The use of serum methadone levels in patients receiving methadone maintenance." Clin Pharmacol Ther 43 (1988):  623-9[4] Finelli PF "Phenytoin and methadone tolerance." N Engl J Med 294 (