INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (245115, 'Benzhydrocodone', 'Nalbuphine', 'Major', 'Concomitant use of opioids with other central nervous system (CNS) depressants including mixed agonist-antagonist or partial agonist opioids may result in profound sedation, respiratory depression, coma, and death.  The risk of hypotension may also be increased.  On the other hand, mixed agonist-antagonist or partial agonist opioids can reduce the pharmacologic effects of other opioid agonists.', NULL, 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate.  If coadministration is necessary (e.g., when initiating a switch from one opioid to the other), the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, and patients should be closely monitored for signs and symptoms of CNS and respiratory depression.  Additional caution is advisable when a mixed agonist-antagonist or partial agonist opioid is added to an existing opioid regimen, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the mixed agonist-antagonist or partial agonist opioid.  A dosage adjustment for one or both drugs may be required.', 'Others', 'Dihydrocodeine, Acetylsalicylic acid, Acetaminophen, Ibuprofen', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288984/', '[1] Moldenhauer CC, Roach GW, Finlayson DC, et al. "Nalbuphine antagonism of ventilatory depression following high-dose fentanyl anesthesia." Anesthesiology 62 (1985):  647-50[2] Gilman AG, eds., Nies AS, Rall TW, Taylor P "Goodman and Gilman''s the Pharmacological Basis of Therapeutics." New York, NY: Pergamon Press Inc.  (1990):[3] Strain EC, Preston KL, Liebson IA, Bigelow GE "Precipitated withdrawal by pentazocine in methadone-maintained volunteers." J Pharmacol Exp Ther 267 (1993):  624-34[4] "Product Information. Nubain (nalbuphine)." Endo Laboratories LLC  (2001):[5] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceuticals Inc  (2001):[6] "Product Information. Talwin NX (pentazocine)." Sanofi Winthrop Pharmaceuticals  (2001):[7] "Product Information. Stadol (butorphanol)." Allscrips Pharmaceutical Company  (2001):[8] "Product Information. Dalgan (dezocine)." Astra-Zeneca Pharmaceuticals  (2001):[9] "Product Information. Suboxone (buprenorphine-naloxone)." Reckitt and Colman Pharmaceuticals Inc  (2002):[10] "Product Information. Subutex (buprenorphine)." Reckitt and Colman Pharmaceuticals Inc  (2002):[11] "Product Information. Butrans (buprenorphine)." Purdue Pharma LP  (2010):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (245116, 'Benzhydrocodone', 'Naloxone', 'Moderate', 'This warning does not apply to the naloxone component in non-injectable formulations of naloxone-containing combination medicines.  Naloxone injection is an antagonist that will reverse the actions of opiates.  This reversal can occur when the opiate drug is being used clinically and when it is being abused.  Physically dependent patients may experience withdrawal symptoms.  Abrupt postoperative opioid reversal has resulted in hypotension, ventricular tachycardia and fibrillation, pulmonary edema, cardiac arrest, encephalopathy, and death.', NULL, 'Patients receiving naloxone injection should be monitored for changes in vital signs, nausea, vomiting, diarrhea, aches, fever, runny nose, sneezing, nervousness, irritability, shivering, abdominal cramps.', NULL, 'Patients receiving naloxone injection should be monitored for changes in vital signs, nausea, vomiting, diarrhea, aches, fever, runny nose, sneezing, nervousness, irritability, shivering, abdominal cramps.', 'Antagonism', 'Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Lactulose, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Sodium sulfate, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288985/', '[1] Gilman AG, eds., Nies AS, Rall TW, Taylor P "Goodman and Gilman''s the Pharmacological Basis of Therapeutics." New York, NY: Pergamon Press Inc.  (1990):[2] "Product Information. Narcan (naloxone)." DuPont Pharmaceuticals  (2001):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0'