INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (245620, 'Tranylcypromine', 'Phenyltoloxamine', 'Moderate', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and antihistamines may result in additive central nervous system depressant effects.', NULL, 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine) should generally be avoided.  If concomitant treatment is unavoidable, patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.  It may be appropriate to monitor some patients for increased anticholinergic effects (e.g., constipation, urinary retention, fever, heat intolerance, blurred vision, confusion, hallucinations, dizziness, palpitations, arrhythmias, syncope), since certain populations such as the elderly and those with underlying organic brain disease tend to be more sensitive to these effects and may be susceptible to anticholinergic intoxication.  It should be noted that the manufacturers of many of the sedating antihistamines (e.g., chlorpheniramine, dexchlorpheniramine, diphenhydramine, pheniramine, promethazine) consider their use within 14 days of MAOIs to be contraindicated.', 'Synergism', 'Vilazodone, Paroxetine, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', NULL, 'Moderate', NULL, 'DDI