INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (245697, 'Dihydrocodeine', 'Brexanolone', 'Moderate', 'Coadministration with central nervous system (CNS) depressants (e.g., alcohol, benzodiazepines, opioids) or antidepressants may enhance the sedative effects of brexanolone and increase the likelihood or severity of sedation-related adverse reactions.', NULL, 'Caution is advised during concomitant use of brexanolone with CNS depressants, antidepressants, or other agents that cause sedation.', NULL, 'Caution is advised during concomitant use of brexanolone with CNS depressants, antidepressants, or other agents that cause sedation.  Patients should be closely monitored for excessive sedation and sudden loss of consciousness and have continuous pulse oximetry monitoring.  During the brexanolone infusion, monitor for sedative effects every 2 hours during planned, non-sleep periods, and immediately stop the infusion if there are signs or symptoms of excessive sedation.  After symptoms resolve, the infusion may be resumed at the same or lower dose as clinically appropriate.  If pulse oximetry reveals hypoxia, immediately stop the infusion and do not resume infusion following resolution of the hypoxia.  Patients should be cautioned against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until sedative effects of brexanolone and other concomitant medications have dissipated.  Patients must be accompanied during interactions with their child(ren) while receiving brexanolone because of the potential for excessive sedation and sudden loss of consciousness.', 'Synergism', 'Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289618/', '[1] "Product Information. Zulresso (brexanolone)." Sage Therapeutics, Inc.  (2019):', 1767369485);
