INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (245882, 'Dihydrocodeine', 'Linezolid', 'Major', 'Coadministration of narcotic analgesics with monoamine oxidase inhibitors (MAOIs) has been associated with rare reports of anxiety, confusion, hypotension, respiratory depression, cyanosis, and coma.  The mechanism of interaction is unknown, but may involve potentiation of central nervous system (CNS) and respiratory depressant effects by MAOIs.', NULL, 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).  At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with opioids.', 'Synergism', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Vancomycin, Metronidazole, Telavancin, Methenamine, Spectinomycin, Nitrofurantoin, More', 'Oliceridine, Naltrexone', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289815/', '[1] Browne B, Linter S "Monoamine oxidase inhibitors and narcotic analgesics: a critical review of the implications for treatment." Br J Psychiatry 151 (1987):  210-2[2] Evans-Prosser CD "The use of pethidine and morphine in the presence of monoamine oxidase inhibitors." Br J Anaesth 40 (1968):  279-82[3] "Product Information. MS Contin (morphine)." Purdue Frederick Company  (2002):[4] "Product Information. Roxicodone (oxycodone)." Roxane Laboratories Inc  (2001):[5] "Product Information. Levo-Dromoran (levorphanol)." Roche Laboratories  (2001):[6] Garbutt JC "Potentiation of pr