INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (245924, 'Dihydrocodeine', 'Mitotane', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels.', NULL, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.  Some manufacturers of products containing codeine advise against the concomitant use of codeine with CYP450 3A4 inducers.  If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended.  Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.  Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Naltrexone, Oxymorphone, Tapentadol, Meperidine, Hydromorphone, Acetaminophen, Dezocine, Opium, Dextropropoxyphene, Ibuprofen, Celecoxib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', N