INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122560, 'Gentamicin (topical)', 'Kanamycin', 'Moderate', 'Topically applied aminoglycosides may be systemically absorbed and have potentially additive effects with systemically administered aminoglycosides. The risk of oto- and nephrotoxicity and other adverse effects of aminoglycosides may be increased.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.g., application to burnt or broken skin; use of large quantities or on large areas for prolonged periods). The normally recommended dosages of these products should not be exceeded.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122698/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365207);
