INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122588, 'Olsalazine', 'Kanamycin', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122726/', '[1] "Product Information. Canasa (mesalamine (5-aminosalicylic acid))." Axcan Scandipharm Inc, Birmingham, AL.[2] Schreiber S. Hamling J, Zehnter E, et al "Renal tubular dysfunction in patients with inflammatory bowel disease treated with aminosalicylate." Gut 40 (1997): 761-6[3] "Product Information. Lialda (mesalamine (5-aminosalicylic acid))." Shire US Inc, Florence, KY.[4] Novis BH, Korzets Z, Chen P, Bernheim J "Nephrotic syndrome after treatment with 5-aminosalicylic acid." Br Med J (Clin Res Ed) 296 (1988): 1442', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122589, 'Oxaliplatin', 'Kanamycin', 'Moderate', 'Theoretically, coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of oxaliplatin, which is primarily eliminated unchanged by the kidney.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of oxaliplatin such as peripheral sensory neuropathies and neutropenia should be considered. Renal function should be closely monitored during therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122727/', '[1] "Product Information. Eloxatin (oxaliplatin)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122590, 'Pamidronic acid', 'Kanamycin', 'Moderate', 'Coadministration of bisphosphonates and aminoglycosides may potentiate the risks of hypocalcemia and nephrotoxicity, particularly when the bisphosphonate is administered intravenously. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium, while aminoglycosides can induce renal tubular damage resulting in renal loss of calcium and magnesium. Hypomagnesemia, in turn, may also lead to peripheral parathyroid hormone resistance and impaired parathyroid hormone secretion, thus compromising the normal homeostatic response to hypocalcemia.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides. Renal function as well as serum calcium and magnesium levels should be monitored during and after treatment, since renal loss of calcium and magnesium can continue for many weeks after cessation of aminoglycosides, as can the effect of bisphosphonates. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasms, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122728/', '[1] Chang JT, Green L, Beitz J "Renal failure with the use of zoledronic acid." N Engl J Med 349 (2003): 1676-9; discussion 1676-9[2] Kanis JA, Preston CJ, Yates AJ, Percival RC, Mundy KI, Russell RG "Effects of intravenous diphosphonates on renal function." Lancet 1 (1983): 1328[3] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Markowitz GS, Fine PL, Stack JI, et al. "Toxic acute tubular necrosis following treatment with zoledronate (Zometa)." Kidney Int 64 (2003): 281-289[5] Janssen Van Doorn K, Neyns B, Van Der Niepen P, Verbeelen D "Pamidronate-related nephrotoxicity (tubulointerstitial nephritis) in a patient with osteolytic bone metastases." Nephron 89 (2001): 467-8[6] "Product Information. Ostac (clodronate)." Hoffmann-La Roche Limited, Mississauga, IA.[7] Zazgornik J, Grafinger P, Biesenbach G, Hubmann R, Fridrik M "Acute renal failure and alendronate." Nephrol Dial Transplant 12 (1997): 2797-8[8] Banerjee D, Asif A, Striker L, Preston RA, Bourgoignie JJ, Roth D "Short-term, high-dose pamidronate-induced acute tubular necrosis: The postulated mechanisms of bisphosphonate nephrotoxicity." Am J Kidney Dis 41 (2003): E18[9] Osullivan TL, Akbari A, Cadnapaphornchai P "Acute renal failure associated with the administration of parenteral etidronate." Ren Fail 16 (1994): 767-73[10] Pedersen-Bjergaard U, Myhre J "Severe hypocalcaemia [corrected] after treatment with diphosphonate and aminoglycoside." Br Med J 302 (1991): 295[11] Lockridge L, Papac RJ, Perazella MA "Pamidronate-associated nephrotoxicity in a patient with Langerhans''s histiocytosis." Am J Kidney Dis 40 (2002): E2[12] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122591, 'Pancuronium', 'Kanamycin', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122729/', '[1] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongation of neuromuscular blocking effect of vecuronium by antibiotics." Anaesthesia 42 (1987): 858-60[2] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[3] Geha DG, Blitt CD, Moon BJ "Prolonged neuromuscular blockade with pancuronium in the presence of acute renal failure: a case report." Anesth Analg 55 (1976): 343-5[4] Giala MM, Paradelis AG "Two cases of prolonged respiratory depression due to interaction of pancuronium with colistin and streptomycin." J Antimicrob Chemother 5 (1979): 234-5[5] Regan AG, Perumbetti PP "Pancuronium and gentamicin interaction in patients with renal failure." Anesth Analg 59 (1980): 393[6] Pittinger CB, Eryasa Y, Adamson R "Antibiotic-induced paralysis." Anesth Analg 49 (1970): 487-501[7] Levanen J, Nordman R "Complete respiratory paralysis caused by a large dose of streptomycin and its treatment with calcium chloride." Ann Clin Res 7 (1975): 47-9[8] "Product Information. Humatin (paromomycin)." Parke-Davis, Morris Plains, NJ.[9] Warner WA, Sanders E "Neuromuscular blockade associated with gentamicin therapy." JAMA 215 (1971): 1153-4[10] Vanacker BF, Van de Walle J "The neuromuscular blocking action of vecuronium in normal patients and in patients with no renal function and interaction vecuronium- tobramycin in renal transplant patients." Acta Anaesthesiol Belg 37 (1986): 95-9[11] Dupuis JY, Martin R, Tetrault JP "Atracurium and vecuronium interaction with gentamicin and tobramycin." Can J Anaesth 36 (1989): 407-11[12] Chapple DJ, Clark JL, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl (1983): s17-22[13] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[14] Burkett L, Bikhazi GB, Thomas KC Jr, Rosenthal DA, Wirta MG, Foldes FF "Mutual potentiation of the neuromuscular effects of antibiotics and relaxants." Anesth Analg 58 (1979): 107-15[15] Lippmann M, Yang E, Au E, Lee C "Neuromuscular blocking effects of tobramycin, gentamicin, and cefazolin." Anesth Analg 61 (1982): 767-70[16] Hasfurther DL, Bailey PL "Failure of neuromuscular blockade reversal after rocuronium in a patient who received oral neomycin." Can J Anaesth 43 (1996): 617-20[17] Giala M, Sareyiannis C, Cortsaris N, Paradelis A, Lappas DG "Possible interaction of pancuronium and tubocurarine with oral neomycin." Anaesthesia 37 (1982): 776', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122592, 'Pantoprazole', 'Kanamycin', 'Moderate', 'Chronic use of proton pump inhibitors (PPIs) may induce hypomagnesemia, and the risk may be increased during concomitant use of diuretics or other agents that can cause magnesium loss. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms. Hypomagnesemia can also cause impaired parathyroid hormone secretion, which may lead to hypocalcemia. In approximately 25% of the cases of PPI-associated hypomagnesemia reviewed by the FDA, the condition did not resolve with magnesium supplementation alone but also required discontinuation of the PPI.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus. Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122730/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Low magnesium levels can be associated with long-term use of proton pump inhibitor drugs (PPIs). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm245011.htm." ([2011 Mar 2]):[2] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122593, 'Pemetrexed', 'Kanamycin', 'Moderate', 'Coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of pemetrexed, which is primarily eliminated unchanged by the kidney via glomerular filtration and active tubular secretion.', NULL, 'Caution is advised if pemetrexed is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if pemetrexed is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of pemetrexed such as bone marrow suppression should be considered. Renal function should be closely monitored during therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122731/', '[1] "Product Information. Alimta (pemetrexed)." Lilly, Eli and Company, Indianapolis, IN.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122594, 'Phenylbutazone', 'Kanamycin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122732/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122595, 'Pipecuronium', 'Kanamycin', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122733/', '[1] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongati