INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122680, 'Aminophylline', 'Ketamine', 'Moderate', 'Cases of extensor-type seizures have been reported in four asthmatic patients who received aminophylline following induction of anesthesia with ketamine. Causality was not clearly determined due to the presence of other anesthetics. Animal studies suggest that the mechanism of this interaction may involve a lowering of the seizure threshold. Theophylline, dyphylline, and oxtriphylline may also interact.', NULL, 'If aminophylline and ketamine must be used concomitantly, caution should be exercised.', NULL, 'If aminophylline and ketamine must be used concomitantly, caution should be exercised.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122829/', '[1] Hirshman CA, Krieger W, Littlejohn G, Lee R, Julien R "Ketamine-aminophylline-induced decrease in seizure threshold." Anesthesiology 56 (1982): 464-7[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Ketalar (ketamine)." JHP Pharmaceuticals, Saddle River, NJ.[4] Peltoniemi MA, Saari TI, Hagelberg NM, Laine K, Neuvonen PJ, Olkkola KT "S-ketamine concentrations are greatly increased by grapefruit juice." Eur J Clin Pharmacol 68 (2012): 979-86[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122681, 'Amoxapine', 'Ketamine', 'Moderate', 'The concomitant use of general anesthetics with tricyclic antidepressants may increase the risk of hypotension and cardiac arrhythmias. The presence of pancuronium may increase the risk of tachyarrhythmias. Enflurane may also increase the risk of seizures when given to patients who have been taking tricyclic antidepressants.', NULL, 'Close monitoring is recommended when these drugs are used together.', NULL, 'Close monitoring is recommended when these drugs are used together.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122830/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365207);
