INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122907, 'Etonogestrel', 'Ketoconazole', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123056/', '[1] Lazar JD, Wilner KD "Drug interactions with fluconazole." Rev Infect Dis 12 Suppl 3 (1990): s327-33[2] Weisberg E "Interactions between oral contraceptives and antifungals antibacterials - Is contraceptive failure the result?." Clin Pharmacokinet 36 (1999): 309-13[3] Pillans PI, Sparrow MJ "Pregnancy associated with a combined oral contraceptive and itraconazole." N Z Med J 106 (1993): 436[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[5] Devenport MH, Crook D, Wynn V, Lees LJ "Metabolic effects of low-dose fluconazole in healthy female users and non-users of oral contraceptives." Br J Clin Pharmacol 27 (1989): 851-9[6] Sinofsky FE, Pasquale SA "The effect of fluconazole on circulating ethinyl estradiol levels in women taking oral contraceptives." Am J Obstet Gynecol 178 (1998): 300-4[7] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[8] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122908, 'Etravirine', 'Ketoconazole', 'Moderate', 'Coadministration of etravirine and itraconazole or ketoconazole may result in increased plasma concentrations of etravirine and decreased plasma concentration of the azole antifungal agent. Etravirine is a substrate as well as inducer of CYP450 3A4, while itraconazole and ketoconazole are both substrates and potent inhibitors of CYP450 3A4. Theoretically, itraconazole and ketoconazole can inhibit the metabolism of etravirine, and etravirine can induce the metabolism of the azole antifungal agents.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with itraconazole or ketoconazole.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with itraconazole or ketoconazole. In addition, dosage adjustments may be required for the azole antifungal agent, depending on other coadministered drugs.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123057/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122909, 'Everolimus', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia may be increased.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided. Some authorities recommend avoiding concomitant use of everolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123058/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Homma S, Takahashi KI, Nihei S, Kato F, Sugihara S, Nunoda S "The successful management of respiratory complications with long-term, low-dose macrolide administration in pediatric heart transplant recipients." Int Heart J (2014):[5] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365207);
