INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122996, 'Macimorelin', 'Ketoconazole', 'Major', 'Macimorelin can cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration. Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123145/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122997, 'Macitentan', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of macitentan, which is primarily metabolized by the isoenzyme. Macitentan was well tolerated with or without ketoconazole in the study, and there were no relevant differences in safety parameters between the treatments.', NULL, 'According to the product labeling, concomitant use of macitentan with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, 'According to the product labeling, concomitant use of macitentan with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Alternative treatment options for pulmonary arterial hypertension are recommended when potent CYP450 3A4 inhibitors are needed as part of HIV treatment.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123146/', '[1] "Product Information. Opsumit (macitentan)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (122998, 'Magaldrate', 'Ketoconazole', 'Moderate', 'INTERVAL: The dissolution of oral ketoconazole requires an acidic environment. Medications that decrease the acidity of the stomach, such as omeprazole or H2 antagonists can reduce the bioavailability of ketoconazole by 75% to 80%. Antacids are also expected to impair ketoconazole absorption.', NULL, 'It is recommended to administer antacids at least 1 hour before or 2 hours after ketoconazole.', NULL, 'It is recommended to administer antacids at least 1 hour before or 2 hours after ketoconazole. Patients should be monitored for altered antifungal effects and the ketoconazole dosage adjusted if necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123147/', '[1] Chin TWF, Loeb M, Fong IW "Effects of an acidic beverage (coca-cola) on absorption of ketoconazole." Antimicrob Agents Chemother 39 (1995): 1671-5[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Van der Meer JW, Keuning JJ "The influence of gastric acidity on the bio-availability of ketoconazole." J Antimicrob Chemother 6 (1980): 552-4[4] Cerner Multum, Inc "ANVISA Bulário Eletrônico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[5] "Product Information. Ketoconazole (ketoconazole)." Mylan Pharmaceuticals Inc, Morgantown, WV.[6] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[7] Brass C, Galgiani JN, Blaschke TF, et al "Disposition of ketoconazole, an oral antifungal, in humans." Antimicrob Agents Chemother 21 (1982): 151-8[8] Carlson JA, Mann HJ, Canafax DM "Effect of pH on disintegration and dissolution of ketoconazole tablets." Am J Hosp Pharm 40 (1983): 1334-6', 1767365207);
