    ) VALUES (1397210, 'Anisindione', 'Colestipol', 'Minor', 'Colestipol may decrease the absorption of warfarin and other anticoagulants. The therapeutic effect of warfarin may be reduced. However, colestipol also may interfere with vitamin K absorption.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
