        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1397211, 'Colestipol', 'Bendroflumethiazide', 'Moderate', 'INTERVAL: Colestipol may decrease the gastrointestinal absorption of thiazide diuretics by binding them in the gut. The interaction has been studied with hydrochlorothiazide (HCTZ) and chlorothiazide.', NULL, 'Thiazide diuretics should be administered as far apart from colestipol as possible, but at least 1 hour before or 4 hours after colestipol.', NULL, 'Thiazide diuretics should be administered as far apart from colestipol as possible, but at least 1 hour before or 4 hours after colestipol. Patients should be monitored for potentially diminished therapeutic effects, and the thiazide dosage adjusted if necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
